        return mutableListOf(
            Receta(1, "Milanesa Napolitana", Dificultad.MEDIA ,Pais.ARGENTINA,"data:image/jpeg;base64,",
                "Ingredientes: \n"+"* Milasesas\n"+"* 150gr de Jamón Cocido\n"+"* 1 Lata de puré de tomate\n"+"* 1 Cebolla\n"+"* 2 Dientes de ajo\n"+"* 2 Hojas de laurel fresco\n"+"* Aceite\n"+"* Sal\n"+"* Pimienta"),
            Receta(2,"Empanadas Salteñas",Dificultad.MEDIA,Pais.ARGENTINA,"data:image/jpeg;base64,",
